import 'package:flutter/material.dart';import 'package:xlo_auction_app/main.dart';import 'package:xlo_auction_app/routes/signIn.dart';class RouteGenerator {  static const String homePage = '/';  static const String signInPage = '/signIn';  RouteGenerator._() {}  static Route<dynamic> generateRoute(RouteSettings settings) {    switch (settings.name) {      case homePage:        return MaterialPageRoute(          builder: (_) => AuthenticationWrapper(),        );      case signInPage:        return MaterialPageRoute(          builder: (_) => SignIn(),        );      default:        throw FormatException("Route not found");    }  }}class RouteException implements Exception {  final String message;  const RouteException(this.message);}